SELECT sb_center.segment_id, sb_center.wu_id, sb_center.replaced_by_wuid, ruType_center.road_type_name as "road_type", ruType_prev.road_type_name as "prev", ruType_next.road_type_name as "next"
	--center seg
	FROM road_unit.segment_boundaries sb_center
		FULL OUTER JOIN road_unit.segments s_center ON sb_center.segment_id = s_center.id 
		FULL OUTER JOIN road_unit.road_type_names ruType_center ON s_center.road_type_id = ruType_center.id 
	--next seg
	INNER JOIN road_unit.segment_boundaries sb_next on sb_center.end_boundary_id = sb_next.start_boundary_id
		FULL OUTER JOIN road_unit.segments s_next ON sb_next.segment_id = s_next.id 
		FULL OUTER JOIN road_unit.road_type_names ruType_next ON s_next.road_type_id = ruType_next.id 
	--prev seg
	INNER JOIN road_unit.segment_boundaries sb_prev on sb_center.start_boundary_id = sb_prev.end_boundary_id
		FULL OUTER JOIN road_unit.segments s_prev ON sb_prev.segment_id = s_prev.id 
		FULL OUTER JOIN road_unit.road_type_names ruType_prev ON s_prev.road_type_id = ruType_prev.id 
WHERE sb_next.replaced_by_wuid IS NULL and sb_prev.replaced_by_wuid IS NULL
AND (ruType_prev.road_type_name = 'NON_CONTROLLED_ACCESS_DIVIDED' OR ruType_prev.road_type_name = 'NON_CONTROLLED_ACCESS_NON_DIVIDED'
	 OR ruType_next.road_type_name = 'NON_CONTROLLED_ACCESS_DIVIDED' OR ruType_next.road_type_name = 'NON_CONTROLLED_ACCESS_NON_DIVIDED')
